    if (!currentSessionId) return;
    const btn = document.getElementById('refresh-session-btn');
    if (btn) btn.classList.add('rotating');

    try {
        await apiCall(`${currentSessionId}/refresh`, 'POST', {});
    } catch (err) {
        console.error(err);
    } finally {
        if(btn) btn.classList.remove('rotating');
    }
}

