+import time
+from playwright.sync_api import sync_playwright
+
+def verify_session_list():
+    with sync_playwright() as p:
+        browser = p.chromium.launch(headless=True)
+        context = browser.new_context()
+        page = context.new_page()
+
+        # Inject mock data by overriding the WebSocket behavior or simply manipulating the DOM/JS state after load.
+        # Since I can execute JS in the page, I will override the `sessions` and `sources` variables and call `renderSessions` and `populateSources`.
+        
+        page.goto("http://localhost:9300")
+
+        # Wait for app to init
+        page.wait_for_timeout(1000)
+
+        # Inject mock data
+        # One session recent (< 24h), one old (> 24h), different sources.
+        mock_data_script = """
+        (function() {
+            window.sources = [
+                { name: 'SourceA' },
+                { name: 'SourceB' }
+            ];
+            
+            const now = new Date();
+            const oneHourAgo = new Date(now.getTime() - (1000 * 60 * 60));
+            const twentyFiveHoursAgo = new Date(now.getTime() - (1000 * 60 * 60 * 25));
+
+            window.sessions = [
+                { 
+                    name: 'SessionRecent_SourceA', 
+                    prompt: 'Recent Source A', 
+                    state: 'RUNNING', 
+                    createTime: oneHourAgo.toISOString(), 
+                    updateTime: oneHourAgo.toISOString(),
+                    sourceContext: { source: 'SourceA' }
+                },
+                { 
+                    name: 'SessionOld_SourceB', 
+                    prompt: 'Old Source B', 
+                    state: 'COMPLETED', 
+                    createTime: twentyFiveHoursAgo.toISOString(), 
+                    updateTime: twentyFiveHoursAgo.toISOString(),
+                    sourceContext: { source: 'SourceB' }
+                },
+                { 
+                    name: 'SessionRecent_SourceB', 
+                    prompt: 'Recent Source B', 
+                    state: 'QUEUED', 
+                    createTime: oneHourAgo.toISOString(), 
+                    updateTime: oneHourAgo.toISOString(),
+                    sourceContext: { source: 'SourceB' }
+                }
+            ];
+            
+            // Re-render
+            populateSources();
+            renderSessions();
+        })();
+        """
+        
+        page.evaluate(mock_data_script)
+        
+        # 1. Verify Default State (Recent only, All Sources)
+        # Should show SessionRecent_SourceA and SessionRecent_SourceB. Should NOT show SessionOld_SourceB.
+        page.wait_for_timeout(500)
+        page.screenshot(path="verification_default.png")
+        print("Default state screenshot taken.")
+
+        # 2. Uncheck 'Recent (<24h)'
+        # Should show all 3 sessions.
+        page.uncheck("#filter-recent")
+        page.wait_for_timeout(500)
+        page.screenshot(path="verification_all_time.png")
+        print("All time screenshot taken.")
+
+        # 3. Filter by SourceA (and All Time)
+        # Should show SessionRecent_SourceA.
+        page.select_option("#filter-source", "SourceA")
+        page.wait_for_timeout(500)
+        page.screenshot(path="verification_source_a.png")
+        print("Source A screenshot taken.")
+
+        browser.close()
+
+if __name__ == "__main__":
+    verify_session_list()

